import React, { useState } from "react";

const NewJournalForm = (props) => {
  let [state, setState] = useState({
    title: "",
    description: "",
  });
  const handleOnChange = (e) => {
    const { name, value } = e.target;
    // console.log(e);
    // console.log(name);
    // console.log(value);
    setState((prevState) => ({
      ...prevState,
      // đoạn này lụm stackoverflow và i do not understand why so don't make a question
      [name]: value,
    }));
    console.log(state[name]);
  };

  return (
    <div>
      <div className="new-journal-wrapper">
        <h2>New Journal</h2>

        <div className="new-journal-form">
          <div className="form-group">
            <label>Title</label>
            <br />
            <input
              className="title-input-field"
              placeholder="Journal Title"
              name="title"
              onChange={handleOnChange}
            ></input>
          </div>
          <div className="form-group">
            <label>Description</label>
            <br />
            <textarea
              className="description-input-field"
              rows="10"
              cols="50"
              placeholder="Journal Description"
              name="description"
              onChange={handleOnChange}
            ></textarea>
          </div>
          <div>
            <button className="button-save-journal">Save</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewJournalForm;
